mapper.insert:
BEGIN
	INSERT INTO ITEM VALUES (ITEM_SEQ.NEXTVAL, #{CategoryID}, #{Name}, #{Content}, #{Price});
	INSERT INTO ITEM_OPTION VALUES(ITEM_OPTION_SEQ.NEXTVAL, #{ItemID}, #{ItemSize}, #{Color}, #{Amount});
END;

mapper.update:
BEGIN
	UPDATE ITEM SET ITEM_CATEGORY_ID = {CategoryID}, NAME = #{Name}, CONTENT = #{Content}, PRICE = #{Price} WHERE ITEM_ID = #{ItemID};
	UPDATE ITEM_OPTION SET AMOUNT = #{Amount} WHERE ITEM_OPTION_ID = #{ItemOptionID};
END;
