mapper.update:
UPDATE ACCOUNT


		, PASSWORD = #{password} 


		, PHONE = #{phone} 


		, EMAIL = #{email}


		, ADDRESS = #{address}


		, ADDRESS_NO = #{address_no}


		, NICKNAME = #{nickname}


WHERE ACCOUNT_ID = #{account_id}

UPDATE SOCIAL_ACCOUNT


		, PHONE = #{phone} 


		, EMAIL = #{email}


		, ADDRESS = #{address}


		, ADDRESS_NO = #{address_no}


		, NICKNAME = #{nickname}


WHERE SOCIAL_ACCOUNT_ID = #{social_account_id}
